import React, {useState , useEffect} from 'react';
import '../../App.css';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import {Form,Col, Button} from 'react-bootstrap';
import {Container, Typography} from "@material-ui/core";
import logo from '../../assets/image/logo.png';
import DoctorNavigation from '../../components/DoctorNavigation';
 
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    marginLeft:200,
    marginBottom:100
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 400,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
}));

export default function Patient() {
  const classes = useStyles();
  const theme = useTheme();

  const [v_id,setV_id]=useState("");
  const [patient_id,setPatient_id]=useState("");
  const [VC_id,setVC_id]=useState("");
  const [vaccine_id,setVaccine_id]=useState("");
  const [doctor_id,setDoctor_id]=useState("");
  const [time,setTime]=useState("");
  const [date,setDate]=useState("");
  const [result,setResult]=useState("");
  const [reason,setReason]=useState("");

  function saveData()
{
  let data={v_id, patient_id,VC_id,vaccine_id,doctor_id, time, data , result, reason}
// console.warn(data);
  fetch("http://localhost:3000/vaccination", {
    method: "POST",
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
    },
    body:JSON.stringify(data)
  }).then((resp)=>{
    // console.warn("resp",resp);;
    resp.json().then((result)=>{
      console.warn("result",result)
    })
  })
}


  return (
    <Container>
      <DoctorNavigation />
      <div className="VCLabel"  >
        <img src={logo} style={{height:100,width:100, marginLeft:130}} />
 <Typography style={{fontFamily:"Carter One",color:"#fff",fontSize:50,marginLeft:70}} >
        Patient
      </Typography>
      </div>
    <Card className="registration">
    <CardMedia
        className={classes.cover}
        // image="https://images.pexels.com/photos/5412/water-blue-ocean.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
        // title="Live from space album cover"
        style={{backgroundColor:"#41649c"}}
      />
      
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography style={{fontSize:30, marginBottom:20,fontFamily: "Carter One", color: "#41649c"}}>
            Registration Form
          </Typography>
        <Form>
          <Form.Row>
          <Form.Group as={Col} controlId={v_id}>
      <Form.Label className="v_id" >Vaccination_id</Form.Label>
      <Form.Control type="text" placeholder="Enter vaccination-id" />
    </Form.Group>
          </Form.Row>
        <Form.Row>
    <Form.Group as={Col} controlId={patient_id}>
      <Form.Label className="patient_id">Patient-id</Form.Label>
      <Form.Control type="text" placeholder="Enter Patient-id" />
    </Form.Group>
    <Form.Group as={Col} controlId={doctor_id}>
      <Form.Label className="doctor_id">Doctor-id</Form.Label>
      <Form.Control type="text" placeholder="Enter doctor-id" />
    </Form.Group>
</Form.Row>
  <Form.Row>
    <Form.Group as={Col} controlId={VC_id}>
      <Form.Label className="VC_id">Vaccination Center ID</Form.Label>
      <Form.Control type="text" placeholder="Enter Vaccination Center Id" />
    </Form.Group>
    <Form.Group as={Col} controlId={vaccine_id}>
      <Form.Label className="vaccine_id">Vaccine-id</Form.Label>
      <Form.Control type="text" placeholder="Enter ID" />
    </Form.Group>
</Form.Row>
<Form.Row>
  <Form.Group controlId={date}>
      <Form.Label className="date">Vaccination Date</Form.Label>
      <Form.Control type="text" placeholder="Enter Vaccination Date" />
    </Form.Group>
    <Form.Group controlId={time}>
      <Form.Label className="time">Vaccination Time</Form.Label>
      <Form.Control type="text" placeholder="Enter Vaccination Date" />
    </Form.Group>
  </Form.Row>
  <Form.Row>
    <Form.Group as={Col} controlId={result}>
      <Form.Label className="result">Vaccination Result</Form.Label>
      <Form.Control type="text" placeholder="i-e Verify/Not Verify" />
    </Form.Group>
    <Form.Group as={Col} controlId={reason}>
      <Form.Label className="reason">Vaccination Purpose</Form.Label>
      <Form.Control type="text"  />
    </Form.Group>
  </Form.Row>

  <Button variant="primary" onClick={saveData}  type="submit">
    Register
  </Button>
</Form>
         
        </CardContent>
        
      </div>
    
    </Card>
    </Container>
  );
}
