import React, {useState, useEffect } from 'react';
import '../../App.css';
import { Container,Row } from "react-bootstrap";
import AdminNavigation from '../../components/AdminNavigation';
import { Line } from "react-chartjs-2";
import axios from "axios";

export default function AdminDashboard(){

  const [chartData, setChartData] = useState({});
  const [vaccine_id, setVaccine_id] = useState([]);
  const [available, setAvailabile] = useState([]);

  const chart = () => {
    let vaccine_id = [];
    let available = [];
    axios.get('http://localhost:3001/vaccine')
      .then(res => {
        console.log(res);
        for (const dataObj of res.data.data) {
          vaccine_id.push(parseInt(dataObj.vaccine_id));
          available.push(parseInt(dataObj.available));
        }
        setChartData({
          labels: vaccine_id,
          datasets: [
            {
              label: "Vaccine Availability",
              data: available,
              backgroundColor: ["rgba(75, 192, 192, 0.6)"],
              borderWidth: 4,
              

            }
          ]
         
        });
      })
      .catch(err => {
        console.log(err);
      });
    console.log(vaccine_id, available);
  };

  useEffect(() => {
    chart();
  }, []);
//  
return (
  <div>
    <Line
      data={{
        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
        datasets: [
          {
            label: '# of votes',
            data: [chartData],
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)',
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
            ],
            borderWidth: 1,
          },
          // {
          //   label: 'Quantity',
          //   data: [47, 52, 67, 58, 9, 50],
          //   backgroundColor: 'orange',
          //   borderColor: 'red',
          // },
        ],
      }}
      height={400}
      width={600}
      options={{
        maintainAspectRatio: false,
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
        legend: {
          labels: {
            fontSize: 25,
          },
        },
      }}
    />
  </div>
)
}
