import React,{useState,useEffect} from 'react';
import {Table,Container,Row,Form,FormControl,Button,Col} from 'react-bootstrap';
import '../../../App.css';
import axios from 'axios';
import { Link } from "react-router-dom";
import DeleteRoundedIcon from '@material-ui/icons/DeleteRounded';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';


// const useStyles = makeStyles({
//   root: {
//     maxWidth: 800,
//    marginLeft:300,

//   },

// });

export default function DoctorRecord(){

  const [doctor, setDoctor] = useState(['']);
  const [search, setSearch] = useState(['']);
  // const {admin_id} = useParams(); 
  // const admin_id = parseInt(sessionStorage.getItem('admin_id'));

   useEffect(() => {
     getData();
   
    },[]);

    const getData=() =>{
      const apiURL=`http://localhost:3001/doctor`
      axios.get (apiURL)
      // fetch(apiUrl)
        .then((res) => {
          console.log()
          setDoctor(res.data)
        })
        .catch(err =>{
          console.log(err)
        })
    }

     const thisdelete = (doctor_id)=>{
      fetch(`http://localhost:3001/doctor/delete/${doctor_id}`,
      {method : "DELETE"
    }).then((result)=>{
      result.json().then((resp)=>{
        alert("Doctor has been Deleted");
        getData();
      })
    })
    }

// const thissearch = (v_id) =>{
//   fetch(`http://localhost:3001/vaccination/search/${v_id}`)
//   .then((data) => {data.json()
//   .then((resp) =>{
//     setSearch(resp.data)
//          console.warn("resp", resp);
         
//       })
//     })
    
// }

  return (
   
    <div>
    {/* <AdminNavigation /> */}

   <div style={{marginTop:150}}>
 
   <Container fluid="xs" className="box" style={{marginLeft:130}}>
   
<Row style={{marginTop:30}} className="justify-content-md-center">
<Col xl={4}>
<h1 className="headStyle">Doctor Record </h1>
</Col>
<Col xl={7}>
<Form inline>
 <FormControl type="text" placeholder="Search"  onChange={e=>{setSearch(e.target.value)}}  className="mr-xl-2" />
 
</Form></Col>
<Col  >
<Table responsive="sm" style={{marginTop:20}} >
<thead style={{backgroundColor: "#41649c" , color:"white"}}>

<tr>
  <th>Doctor_id</th>
  <th>Vaccination Center_id</th>
  <th>First Name</th>
  <th>Last Name</th>
  <th>Email</th>
  <th>Password</th>
  <th>Address</th>
  <th>Phone Number</th>
  <th>Qualification</th>
  <th>Gender</th>
  <th>DOB</th>
  <th>Religion</th>
  <th>City </th>
  <th>State </th>
  <th>Zip </th>
  <th colspan="2">Action </th>
</tr>
</thead>
<tbody>
{doctor.filter((item)=>{
    if(search==""){
      return item
    }else if (item.doctor_id.toLowerCase().includes(search.toLowerCase())){
      return item
    }
  }).map(item =>(
