import React from 'react';
import AdminNavigation from '../../components/AdminNavigation'
import {Table,Container,Row,Form,FormControl,Button,Col} from 'react-bootstrap';
import '../../App.css';
import DeleteRoundedIcon from '@material-ui/icons/DeleteRounded';
import EditIcon from '@material-ui/icons/Edit';

class VaccinationRecord extends React.Component {

constructor(){
    super();
    this.state={
        items:[],
        isLoaded:false,
    }
}
  componentDidMount() {
    const apiUrl = 'http://localhost:3001/vaccination';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })}
    //   console.log('This is your data', data));
    
  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
    return( 
        <div>
         <AdminNavigation />
     
        <div style={{marginTop:150}}>
      
        <Container fluid="xs" className="box" >
        
    <Row style={{marginTop:30}} className="justify-content-md-center">
    <Col xl={4}>
    <h1 className="headStyle">Vaccination Record </h1>
    </Col>
    <Col xl={9}>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-xl-2" />
      <Button variant="outline-primary">Search</Button>
    </Form></Col>
    <Col  >
<Table responsive="sm" style={{marginTop:20}} >
<thead style={{backgroundColor: "#41649c" , color:"white"}}>
     
     <tr>
       <th>Vaccination_id</th>
       <th>Patient_id</th>
       <th>Vaccination Center_id</th>
       <th>Vaccine_id</th>
       <th>doctor_id</th>
       <th>Vaccination Time</th>
       <th>Vaccination Date</th>
       <th>Vaccination Result</th>
       <th>Vaccination Reason</th>
       <th> </th>
       <th> </th>
     </tr>
   </thead>
    <tbody>
      {items.map(item =>(
        <tr  key={item.id}>
          <td>{item.v_id}</td>
          <td>{item.patient_id}</td>
          <td>{item.vc_id}</td>
          <td>{item.doctor_id}</td>
          <td>{item.time}</td>
          <td>{item.date}</td>
          <td>{item.result}</td>
          <td>{item.reason}</td>
          <td ><DeleteRoundedIcon style={{color: "#41649c"}}/> </td>
          <td> <EditIcon  style={{color: "#41649c"}}/></td>
        </tr>
          ))}
      </tbody>
  </Table>
  </Col>
  </Row>
  </Container>
  </div>
</div>
    )
}
  }
}
export default VaccinationRecord;
