import React, {useState} from "react";
import axios from 'axios';
import '../App.css'
import { Card, Container,Nav,Button,Form } from "react-bootstrap";
import Header from '../components/Header';
import {useHistory} from 'react-router-dom';

export default function VerificationLogin(props){
    const history= useHistory();
    
    const [vfc_id, setVfc_id] = useState("");
    const [password, setPassword] = useState("");
 
    const [loginStatus, setLoginStatus] = useState("");

    const login = ()=>{
      if(!vfc_id || !password){
        alert("Provide id/password");
      }
      else{
      axios.post("http://localhost:3001/VFRegistration/login",{
        vfc_id :  vfc_id,
        password : password,
      }).then((response)=>{
       if(response.data.message){
       alert("Please Provide id/password")
         setLoginStatus(response.data.message);
        
       }else{
         setLoginStatus(response.data[0].vfc_id);
         props.history.push('/VerificationDashboard')
       }
     
      });
    }
    };



    return(
        <div>
             <Header />
        <Container fuild="xl">
            <Card className="myCard">
  <Card.Header>
    <Nav variant="tabs">
      <Nav.Item>
        <Nav.Link href="/AdminLogin" >Admin</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VCLogin">Vaccination Center</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VerificationLogin" active >
          Verification Center
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VerifierLogin">
          Verifier
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/DoctorLogin"  >
          Doctor
        </Nav.Link>
      </Nav.Item>
    </Nav>
  </Card.Header>
  <Card.Body>
    <Card.Title className="cardTitle">Verification Center</Card.Title>
    <Card.Text>
    <Form>
  <Form.Group controlId="formBasicIDl">
    <Form.Label className="formLabel">VFC-id</Form.Label>
    <Form.Control type="text" onChange={(e)=>{setVfc_id(e.target.value)}}  placeholder="Enter ID" />
    
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label className="formLabel">Password</Form.Label>
    <Form.Control type="password" onChange={(e)=>{setPassword(e.target.value)}}  placeholder="Password" />
  </Form.Group>
  
</Form>
    </Card.Text>
    <Button onClick={login} className="loginBtn" variant="primary">Login</Button>
  </Card.Body>
</Card>
        </Container>
        </div>
    )
}
