import React,{useState,useEffect} from 'react';
import '../../App.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import AdminNavigation from '../../components/AdminNavigation';
import Button from '@material-ui/core/Button';
import {Container} from '@material-ui/core';
import {Row,Col,Image} from 'react-bootstrap';
import logo from '../../assets/image/logo.png';
import Typography from '@material-ui/core/Typography';
import {useParams  } from "react-router-dom";
import {withRouter  } from "react-router";
import loginID from '../AdminLogin';
import axios from 'axios';


// const useStyles = makeStyles({
//   root: {
//     maxWidth: 800,
//    marginLeft:300,

//   },

// });

export default function AdminProfile(props){
  
  const [admin, setAdmin] = useState(['']);

  const {propAdmin} = useParams(); 
  // const adminID = props.propAdmin;
  // const admin_id = parseInt(sessionStorage.getItem('admin_id'));

  const apiCall = () => {
    let formData = new FormData();
    const admin_id = localStorage.getItem("admin_id");

    formData.append("admin_id", admin_id);

    const config = {
      headers: { "content-type": "application/json" },
    };
    axios
    .post(`http://localhost:3001/admin/${parseInt(props.propAdmin)}`, formData, config)
    .then((response) => {
        this.setState({ response: response });
    }).catch((error) => {
      console.err(error);
    });
  }
   useEffect(() => {
    apiCall();
  //  const apiURL=`http://localhost:3001/admin/${parseInt(props.propAdmin)}`
  //   axios.get (apiURL)
  //   // fetch(apiUrl)
  //     .then((res) => {
  //       console.log()
  //       setAdmin(res.data)
  //     })
  //     .catch(err =>{
  //       console.log(err)
  //     })
  //   alert(apiURL);
  //   console.log(admin)
   
  //   },[props.propAdmin])
   },[])

  return (
   
<Container>
  <AdminNavigation />
    <Card className="adminCard" >
    
        <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="140"
          image="https://images.pexels.com/photos/5412/water-blue-ocean.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
          title="Contemplative Reptile"
        />
      {admin.data.map((item) => {
         
          <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:200,marginTop:-50,color:"#fff"}}>
             {item.name}
            
           </Typography>
         
         })}
        <CardContent>
        <Row>
         <Col xl={4} id="photo">
       <Image src="https://hairstylecamp.com/wp-content/uploads/short-businessmen-haircut.jpg"
        roundedCircle
       style={{width:130,height:130, marginTop:-100,marginLeft:30}}
       />
       <img src={logo} alt=" " style={{height:40,width:40, marginLeft:-50}} />
       <Card style={{marginTop:40,height:250, marginBottom:40}}>
         <CardContent>
           <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:20}}>
             Highlights
           </Typography> <hr />
           
         </CardContent>
       </Card>
       <Button style={{marginLeft:60,marginBottom:20}} variant="contained" size="small" color="primary">
          Edit Profile
        </Button>
         </Col>
         <Col xl={8}>
           

           <Card>
           {admin.map(item =>(
           item.length > 0 && (
           <CardContent key={item.admin.id} style={{fontSize:17}}>
          
             <p id="admin_id">Admin-ID: <span style={{marginLeft:20}}> {item.admin_id} </span></p><hr />
             <p id="name">FullName: <span style={{marginLeft:20}}> {item.name} </span></p><hr />
             <p id="password">Password: <span style={{marginLeft:20}}> {item.password} </span></p><hr />
             <p id="email">Email: <span style={{marginLeft:20}}>{item.email} </span> </p><hr />
             <p id="address">Address: <span style={{marginLeft:20}}> {item.address} </span></p><hr />
             <p id="phone">Phone#: <span style={{marginLeft:20}}> {item.phone} </span></p><hr />
             <p id="profession">Profession: <span style={{marginLeft:20}}>{item.profession} </span> </p><hr />
             <p id="gender">Gender: <span style={{marginLeft:20}}>{item.gender} </span> </p><hr />
             <p id="dob">DOB: <span style={{marginLeft:20}}>{item.dob} </span> </p><hr />
             <p id="religion">Religion: <span style={{marginLeft:20}}>{item.religion} </span> </p><hr />
             <p id="city">City: <span style={{marginLeft:20}}> {item.city} </span></p><hr />
             <p id="state">State:<span style={{marginLeft:20}}>{item.state} </span> </p>
          

             </CardContent>
           )
   
   ))}
           </Card>
           

         </Col>
         </Row>
        </CardContent>
        
      <CardActions>
        
      </CardActions>
    </Card>
    </Container>
  );
    }
