  const [admin, setAdmin] = useState([]);

  const id = useParams(); 
const adID= parseInt(props.sessionStorage.getItem('id'))
   useEffect(() => {
   const apiURL=`http://localhost:3001/admin/${adID}`
    axios.get (apiURL)
    // fetch(apiUrl)
      .then((res) => {
        console.log()
        setAdmin(res.data)
      })
      .catch(err =>{
        console.log(err)
      })
    alert(adID);
    },[id])

