<Container>
  <AdminNavigation />
    <Card className="adminCard" >
    
        <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="140"
          image="https://images.pexels.com/photos/5412/water-blue-ocean.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
          title="Contemplative Reptile"
        />
       {admin.map(item =>(
          <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:200,marginTop:-50,color:"#fff"}}>
             {item.name}
           </Typography>
        ))}
        <CardContent>
        <Row>
         <Col xl={4} id="photo">
       <Image src="https://hairstylecamp.com/wp-content/uploads/short-businessmen-haircut.jpg"
        roundedCircle
       style={{width:130,height:130, marginTop:-100,marginLeft:30}}
       />
       <img src={logo} alt=" " style={{height:40,width:40, marginLeft:-50}} />
       <Card style={{marginTop:40,height:250, marginBottom:40}}>
         <CardContent>
           <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:20}}>
             Highlights
           </Typography> <hr />
           
         </CardContent>
       </Card>
       <Button style={{marginLeft:60,marginBottom:20}} variant="contained" size="small" color="primary">
          Edit Profile
        </Button>
         </Col>
         <Col xl={8}>
           <Card>
           {admin.map(item =>
           <CardContent key={item.admin_id} style={{fontSize:17}}>
          
             <p id="admin_id">Admin-ID: <span style={{marginLeft:20}}> {item.admin_id} </span></p><hr />
             <p id="name">FullName: <span style={{marginLeft:20}}> {item.name} </span></p><hr />
             <p id="password">Password: <span style={{marginLeft:20}}> {item.password} </span></p><hr />
             <p id="email">Email: <span style={{marginLeft:20}}>{item.email} </span> </p><hr />
             <p id="address">Address: <span style={{marginLeft:20}}> {item.address} </span></p><hr />
             <p id="phone">Phone#: <span style={{marginLeft:20}}> {item.phone} </span></p><hr />
             <p id="profession">Profession: <span style={{marginLeft:20}}>{item.profession} </span> </p><hr />
             <p id="gender">Gender: <span style={{marginLeft:20}}>{item.gender} </span> </p><hr />
             <p id="dob">DOB: <span style={{marginLeft:20}}>{item.dob} </span> </p><hr />
             <p id="religion">Religion: <span style={{marginLeft:20}}>{item.religion} </span> </p><hr />
             <p id="city">City: <span style={{marginLeft:20}}> {item.city} </span></p><hr />
             <p id="state">State:<span style={{marginLeft:20}}>{item.state} </span> </p>
          

             </CardContent>
    )}

           </Card>


         </Col>
         </Row>
        </CardContent>
        
      <CardActions>
        
      </CardActions>
