import React, {useState} from "react";
import axios from 'axios';
import '../App.css'
import { Card, Container,Nav,Button,Form } from "react-bootstrap";
import Header from '../components/Header';
import {useHistory} from 'react-router-dom';
// import Login from '../layout/Login';


export default function AdminLogin(props){
  
 
    const history= useHistory();
  
    
    const [admin_id, setAdmin_id] = useState("");
    const id=admin_id.slpice(0,3);
    console.log(id)
    const [password, setPassword] = useState("");
    const [vc_id, setVc_id]= useState("");
 
    const [loginStatus, setLoginStatus] = useState("");

    const login = ()=>{
      if(!admin_id || !password){
        alert("Provide id/password");
      }
      else{
      axios.post("http://localhost:3001/admin/login",{
        admin_id : admin_id,
        password : password,
      }).then((response)=>{
       if(response.data.message){
       alert("Please Provide correct id/password")
         setLoginStatus(response.data.message);
         }else{
         setLoginStatus(response.data[0].admin_id);
         props.history.push('/AdminDashboard')
       }
      
      });
    }

    };

    return(
        <div>
             <Header />
        <Container fuild="xl">
            <Card className="myCard">
  {/* <Card.Header>
  <Nav variant="tabs">
      <Nav.Item>
        <Nav.Link href="/AdminLogin" active>Admin</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VCLogin">Vaccination Center</Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VerificationLogin" >
          Verification Center
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/VerifierLogin">
          Verifier
        </Nav.Link>
      </Nav.Item>
      <Nav.Item>
        <Nav.Link href="/DoctorLogin">
          Doctor
        </Nav.Link>
      </Nav.Item>
    </Nav>
  </Card.Header> */}
  <Card.Body>
    <Card.Title className="cardTitle">Admin Login</Card.Title>
    <Card.Text>
    <Form>
  <Form.Group controlId="formBasicIDl">
    <Form.Label className="formLabel">Admin-id</Form.Label>
    <Form.Control type="text" onChange={(e)=>{setAdmin_id(e.target.value)}}    placeholder="Enter ID" />
    
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label className="formLabel">Password</Form.Label>
    <Form.Control type="password" onChange={(e)=>{setPassword(e.target.value)}}  placeholder="Password" />
  </Form.Group>
  
</Form>
    </Card.Text>
    <Button onClick={login} className="loginBtn" variant="primary">Login</Button>
  </Card.Body>
</Card>
        </Container>
        </div>
    )
}

