import React from 'react';
import {Table,Container,Row,Col} from 'react-bootstrap';
import '../../../App.css';
import { Link } from "react-router-dom";
import DeleteRoundedIcon from '@material-ui/icons/DeleteRounded';
import EditIcon from '@material-ui/icons/Edit';

class PatientRecord extends React.Component {

constructor(){
    super();
    this.state={
        items:[],
        isLoaded:false,
    }
}
componentDidMount() {
  this.getData()
  }
  getData(){
    const apiUrl = 'http://localhost:3001/patientRegistration';
  fetch(apiUrl)
    .then((response) => response.json())
    .then(json =>{
        this.setState({
            isLoaded:true,
            items : json
        })
    })
  } 
  componentDidMount() {
    const apiUrl = 'http://localhost:3001/patientRegistration';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })}
    //   console.log('This is your data', data));
    
    delete(patient_id){
      fetch(`http://localhost:3001/patientRegistration/delete/${patient_id}`,
      {method : "DELETE"
    }).then((result)=>{
      result.json().then((resp)=>{
        alert("Patient has been Deleted");
        this.getData()
      })
    })
    }


  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
    return( 
        <div>
        
     
        <div style={{marginTop:150}}>
      
        <Container fluid="xs" className="box" style={{marginLeft:310}}>
        
    <Row style={{marginTop:30}} className="justify-content-md-center">
    <Col xl={7}>
    <h1 className="headStyle">Patient Record </h1>
    </Col>
    {/* <Col xl={9}>
    <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-xl-2" />
      <Button variant="outline-primary">Search</Button>
    </Form></Col> */}
    <Col  >
<Table responsive="sm" style={{marginTop:20}} >
<thead style={{backgroundColor: "#41649c" , color:"white"}}>
     
     <tr>
       <th>Patient_id</th>
       <th>Vaccination Center_id</th>
       <th>First Name</th>
       <th>Last Name</th>
       <th>Father's Name</th>
       <th>Email</th>
       <th>Password</th>
       <th>Address</th>
       <th>Phone Number</th>
       <th>Qualification</th>
       <th>Date Of Registration</th>
       <th>Gender</th>
       <th>DOB</th>
       <th>Martial Status</th>
       <th>CNIC </th>
       <th>Religion</th>
       <th>City </th>
       <th>State </th>
       <th>Zip </th>
       <th colspan="2">Action </th>
     </tr>
   </thead>
    <tbody>
      {items.map(item =>(
        <tr  key={item.id}>
          <td>{item.patient_id}</td>
          <td>{item.vc_id}</td>
          <td>{item.f_name}</td>
          <td>{item.l_name}</td>
          <td>{item.father_name}</td>
          <td>{item.email}</td>
          <td>{item.password}</td>
          <td>{item.address}</td>
          <td>{item.phone}</td>
          <td>{item.qualification}</td>
          <td>{item.date}</td>
          <td>{item.gender}</td>
          <td>{item.dob}</td>
          <td>{item.martial_status}</td>
          <td>{item.cnic}</td>
          <td>{item.religion}</td>
          <td>{item.city}</td>
          <td>{item.state}</td>
          <td>{item.zip}</td>
          <td ><DeleteRoundedIcon onClick={()=>this.delete(item.patient_id)} style={{color: "#41649c"}}/> </td>
          <td><Link className="mr-2"  to={`/PatientEdit/${item.patient_id}`}><EditIcon  style={{color: "#41649c"}}/></Link>
          </td>        </tr>
          ))}
      </tbody>
  </Table>
  </Col>
  </Row>
  </Container>
  </div>
</div>
    )
}
  }
}
export default PatientRecord;
