export default class VCViewRecord extends React.Component {

constructor(props){
  super(props);
  this.state={
    vc: ['']
  }
}

componentDidMount(){
  console.log(this.props.match.params);
  let {vc_id} = this.props.match.params;
  fetch(`http://localhost:3001/VCRegistration/search/${vc_id}`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json"
    }
  }).then((response) => response.json())
  .then((responseJson) => {
    this.setState({ 
    vc: responseJson.data });
        console.warn(responseJson)
      })
      
    .catch(err =>{
      console.log(err)
    })
}

  
   
render(){
  const {vc} = this.state;
  return (
    
    <div className="container py-4" className="myViewCard" style={{marginBottom:30}}>
      <Header />
      <h1 className="display-4" className="headStyle">Vaccination Center Id: vcc{vc.vc_id}</h1>
      <hr />
  
      <Card style={{height:40, fontSize:30}}>
      <Card.Body>
      <Card.Title>Vaccination Center Name:{vc.name} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>Email: {vc.email} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>Password: {vc.password} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>Address: {vc.address} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>PhoneNo: {vc.phone} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>Registration No: {vc.reg_no} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>City: {vc.city} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>City: {vc.city} </Card.Title>
      </Card.Body >
      </Card>
      <Card >
      <Card.Body>
      <Card.Title>Zip: {vc.zip} </Card.Title>
      </Card.Body >
      </Card>

      
      {/* <ul className="list-group w-50">
        <li className="list-group-item">Vaccination Center Name: {vc.name}</li>
        <li className="list-group-item">Email: {vc.email}</li>
        <li className="list-group-item">Password: {vc.password}</li>
        <li className="list-group-item">Address: {vc.address}</li>
        <li className="list-group-item">PhoneNo: {vc.phone}</li>
        <li className="list-group-item">Registration No: {vc.req_no}</li>
        <li className="list-group-item">City: {vc.city}</li>
        <li className="list-group-item">City: {vc.city}</li>
        <li className="list-group-item">Zip: {vc.zip}</li>
      </ul> */}
      <Link className="btn btn-primary" to="/VCRecordAD" style={{marginTop:20}}>
        back 
      </Link>
    </div>
  );
};

}
