import React from 'react';
import '../../App.css';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import AdminNavigation from '../../components/AdminNavigation';
import Button from '@material-ui/core/Button';
import {Container} from '@material-ui/core';
import {Row,Col,Image} from 'react-bootstrap';
import logo from '../../assets/image/logo.png';
import Typography from '@material-ui/core/Typography';

// const useStyles = makeStyles({
//   root: {
//     maxWidth: 800,
//    marginLeft:300,

//   },

// });

export default class AdminProfile extends React.Component {
  // const classes = useStyles();

  constructor(){
    super();
    this.state={
        items:[],
        isLoaded:false,
    }
}
  componentDidMount() {
    const apiUrl = 'http://localhost:3001/admin';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })}
    //   console.log('This is your data', data));
    
  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
  return (
<Container>
  <AdminNavigation />
    <Card className="adminCard" >
        <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="140"
          image="https://images.pexels.com/photos/5412/water-blue-ocean.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
          title="Contemplative Reptile"
        />
          <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:200,marginTop:-50,color:"#fff"}}>
             MR. XYZ
           </Typography>
        <CardContent>
        <Row>
         <Col xl={4} id="photo">
       <Image src="https://hairstylecamp.com/wp-content/uploads/short-businessmen-haircut.jpg"
        roundedCircle
       style={{width:130,height:130, marginTop:-100,marginLeft:30}}
       />
       <img src={logo} alt=" " style={{height:40,width:40, marginLeft:-50}} />
       <Card style={{marginTop:40,height:250, marginBottom:40}}>
         <CardContent>
           <Typography style={{fontFamily:'Carter One', fontSize:30,marginLeft:20}}>
             Highlights
           </Typography> <hr />
           
         </CardContent>
       </Card>
       <Button style={{marginLeft:60,marginBottom:20}} variant="contained" size="small" color="primary">
          Edit Profile
        </Button>
         </Col>
         <Col xl={8}>
           <Card>
           {items.map(item =>(
           <CardContent key={item.id}>
          
             <p id="admin_id">Admin-ID: <span style={{marginLeft:20, fontSize:20}}> {item.admin_id} </span></p><hr />
             <p id="name">FullName: {item.name} </p><hr />
             <p id="password">Password: {item.password}</p><hr />
             <p id="email">Email: {item.email}</p><hr />
             <p id="address">Address: {item.address}</p><hr />
             <p id="phone">Phone#: {item.phone}</p><hr />
             <p id="profession">Profession: {item.profession}</p><hr />
             <p id="gender">Gender: {item.gender}</p><hr />
             <p id="dob">DOB: {item.dob}</p><hr />
             <p id="religion">Religion: {item.religion}</p><hr />
             <p id="city">City: {item.city}</p><hr />
             <p id="state">State: {item.state}</p>
          

             </CardContent>
 ))}

           </Card>


         </Col>
         </Row>
        </CardContent>
        
      <CardActions>
        
      </CardActions>
    </Card>
    </Container>
  );
    }
}
}
