import React from 'react';
import AdminNavigation from '../../components/AdminNavigation'
import {Table,Container,Row} from 'react-bootstrap';
import '../../App.css';

class VaccineRecord extends React.Component {

constructor(){
    super();
    this.state={
        items:[],
        isLoaded:false,
    }
}
  componentDidMount() {
    const apiUrl = 'http://localhost:3001/vaccine';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })}
    //   console.log('This is your data', data));
    
  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
    return( 
        <div>
         <AdminNavigation />
     
        <div style={{marginTop:100}}>
      
        <Container fluid="xs" className="box" >

    <Row className="justify-content-md-center" style={{marginTop:40}}>
    <h1 style={{color: '#41649c' }}>Vaccine Record <hr></hr></h1>
<Table responsive="sm">
<thead style={{backgroundColor: "#41649c" , color:"white"}}>
     
     <tr>
       <th>Vaccine_id</th>
       <th>Vaccine Name</th>
       <th>Vaccine Manufacturer</th>
       <th>Vaccine BatchNo</th>
       <th>Production Date</th>
       <th>Expiry Date</th>
       <th>Vaccine Availability</th>
     </tr>
   </thead>
    <tbody>
      {items.map(item =>(
        <tr  key={item.id}>
          <td>{item.vaccine_id}</td>
          <td>{item.title}</td>
          <td>{item.manf}</td>
          <td>{item.batch}</td>
          <td>{item.p_date}</td>
          <td>{item.vc_id}</td>
          <td>{item.available}</td>
        </tr>
          ))}
      </tbody>
  </Table>
  </Row>
  </Container>
  </div>
</div>
    )
}
  }
}
export default VaccineRecord;
