import React from 'react';
import {Table,Container,Row,Form,FormControl,Button,Col} from 'react-bootstrap';
import '../../../App.css';
import axios from "axios";
import { Link } from "react-router-dom";
import DeleteRoundedIcon from '@material-ui/icons/DeleteRounded';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';

class VaccinationRecord extends React.Component {

constructor(){
    super();
    this.state={
        items:[],
        searchData:[],
        isLoaded:false,
        open: false
    }
}
search(v_id){
  fetch(`http://localhost:3001/vaccination/search/${v_id}`).then((data) => {data.json()
  .then((resp) =>{
      // this.setState({ searchData:resp})
         console.warn("resp", resp);
         
      })
    })
}

  componentDidMount() {
    this.getData()
    }
    getData(){
      const apiUrl = 'http://localhost:3001/vaccination';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })
    }
    //   console.log('This is your data', data));

    delete(v_id){
      fetch(`http://localhost:3001/vaccination/delete/${v_id}`,
      {method : "DELETE"
    }).then((result)=>{
      result.json().then((resp)=>{
        alert("Vaccination has been Deleted");
        this.getData()
      })
    })
    }

    

  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
    return( 
        <div>
     
        <div style={{marginTop:150}}>
      
        <Container fluid="xs" className="box" >
        
    <Row style={{marginTop:30}} className="justify-content-md-center">
    <Col xl={5}>
    <h1 className="headStyle">Vaccination Record </h1>
    </Col>
    <Col xl={8}>
    <Form inline>
      <FormControl type="text"  onChange={e=>(e.target.id)}  placeholder="Search" id="search"  className="mr-xl-2" />
      <Button variant="outline-primary" onClick={this.search()}  >Search</Button>
      

    </Form></Col>
    <Col  >
<Table responsive="sm" style={{marginTop:20}} >
<thead style={{backgroundColor: "#41649c" , color:"white"}}>
     
     <tr>
       <th>Vaccination_id</th>
       <th>Patient_id</th>
       <th>Vaccination Center_id</th>
       <th>Vaccine_id</th>
       <th>doctor_id</th>
       <th>Vaccination Time</th>
       <th>Vaccination Date</th>
       <th>Vaccination Result</th>
       <th>Vaccination Reason</th>
       <th colspan="2">Action </th>
      
     </tr>
   </thead>
    <tbody>
      {items.map(item =>(
        <tr  key={item.id}>
          <td>{item.v_id}</td>
          <td>{item.patient_id}</td>
          <td>{item.vc_id}</td>
          <td>{item.vaccine_id}</td>
          <td>{item.doctor_id}</td>
          <td>{item.time}</td>
          <td>{item.date}</td>
          <td>{item.result}</td>
          <td>{item.reason}</td>
          <td>
          {/* <Link className="mr-2" to={`/VaccinationView/${item.v_id}`} ><VisibilityIcon style={{color: "#41649c"}}/></Link>  */}
          <Link className="mr-2"  to={`/VaccinationEdit/${item.v_id}`}><EditIcon  style={{color: "#41649c"}}/></Link>
          </td> <td><Link className="mr-2" onClick={()=>this.delete(item.v_id)} ><DeleteRoundedIcon style={{color: "#41649c"}}/></Link>  
          </td>
        </tr>
          ))}
      </tbody>
  </Table>
  </Col>
  </Row>
  </Container>
  </div>
</div>
    )
}
  }
}
export default VaccinationRecord;
