import React from 'react';
import AdminNavigation from '../../components/AdminNavigation'
import {Table,Container,Row} from 'react-bootstrap';
import '../../App.css';

class VaccineRecord extends React.Component {

constructor(){
    super();
    this.state={
        items:[],
        isLoaded:false,
    }
}
  componentDidMount() {
    const apiUrl = 'http://localhost:3001/vaccine';
    fetch(apiUrl)
      .then((response) => response.json())
      .then(json =>{
          this.setState({
              isLoaded:true,
              items : json
          })
      })}
    //   console.log('This is your data', data));
    
  render() {
    var { isLoaded, items} = this.state;
    if(!isLoaded){
        return <div>Loading....</div>
    }
    else{
    return( 
        <div>
         <AdminNavigation />
     
        <div >
      
        
          
   



    {/* <ul style={{marginLeft:400}}>
        {items.map(item =>(
            <li key={item.id}>

                Vaccine_id:{item.vaccine_id}
                   Vaccine Name:{item.title}
                   Manufacturer:{item.manf}
                   BactchNo:{item.batch}
                   Production Date:{item.p_date}
                   Vaccination Center Id:{item.vc_id}
                   Vaccine Availability:{item.available}
            </li>

            
        ))}
    </ul> */}

    {/* {
        this.state.data ?
        this.state.data.map((item,i)=>
        <div><p>
        {i}--
        {item.vaccine_id}
        {item.title}
        </p></div>)
        :
        null
    } */}




<Container fluid="xs" className="box" >

    <Row className="justify-content-md-center" style={{marginTop:100}}>
    <h1 style={{marginLeft:100 }}>Vaccine Record </h1>
<Table responsive="sm">
<thead style={{backgroundColor: "#41649c" , color:"white"}}>
     
     <tr>
       <th>Vaccine_id</th>
       <th>Vaccine Name</th>
       <th>Vaccine Manufacturer</th>
       <th>Vaccine BatchNo</th>
       <th>Production Date</th>
       <th>Expiry Date</th>
       <th>Vaccine Availability</th>
     </tr>
   </thead>
    <tbody>
      {items.map(item =>(
        <tr  key={item.id}>
          <td>{item.vaccine_id}</td>
          <td>{item.title}</td>
          <td>{item.manf}</td>
          <td>{item.batch}</td>
          <td>{item.p_date}</td>
          <td>{item.vc_id}</td>
          <td>{item.available}</td>
        </tr>
          ))}
      </tbody>
  </Table>
  </Row>
  </Container>
  </div>
</div>
    )
}
  }
}
export default VaccineRecord;
