import React, {useEffect, useState } from 'react';
import '../../../App.css';
import axios from 'axios';
import { useHistory, useParams } from "react-router-dom";
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import {Form,Col,Button} from 'react-bootstrap';
import {Container, Typography} from "@material-ui/core";
import logo from '../../../assets/image/logo.png';
import Header from '../../../components/Header';
 
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    marginLeft:200,
    marginBottom:100
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 400,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
}));

export default function PatientEdit() {
  const classes = useStyles();
  const theme = useTheme();
  const { id } = useParams();
  let history = useHistory();
  // const [patient, setPatient] = useState({
  //           patient_id: '',
  //           vc_id: '',  
  //           f_name:'',
  //           l_name:' ',
  //           father_name:'',
  //           email:'',
  //           password:'',
  //           address: '',
  //           phone:'',
  //           date :'',
  //           qualification:'',
  //           gender:'', 
  //           dob:'', 
  //           martial_status:'', 
  //           cnic:'', 
  //           religion :'',
  //           city:'', 
  //           state:'',
  //           zip:''

           
  // });

  // const { patient_id , vc_id ,f_name , l_name , father_name, email, password,date, address, phone, qualification, gender, 
  // dob,martial_status,cnic, religion,city,state,zip} = patient;
  // const onInputChange = e => {
  //   setPatient({ ...patient, [e.target.name]: e.target.value });
  // };
  const [vc_id, setVC_id] = useState([]);
  const [f_name, setFname] = useState("");
  const [l_name, setLname] = useState("");
  const [father_name, setFather_name] = useState();
  const [email, setEmail] = useState([]);
  const [password, setPassword] = useState("");
  const [date, setDate] = useState("");
  const [address, setAddress] = useState();
  const [phone, setPhone] = useState([]);
  const [qualification, setQualification] = useState("");
  const [gender, setGender] = useState("");
  const [dob, setDOB] = useState();
  const [martial_status, setMartial_Status] = useState([]);
  const [cnic, setCNIC] = useState("");
  const [religion, setReligion] = useState("");
  const [city, setCity] = useState();
  const [state, setState] = useState([]);
  const [zip, setZip] = useState("");

  useEffect(()=>{
    loadPatient();
  },[]);



  const onSubmit = async e => {
    e.preventDefault();
   
  let patient={vc_id,f_name , l_name , father_name, email, password,date, address, phone, qualification, gender, 
    dob,martial_status,cnic, religion,city,state,zip}
    fetch("http://localhost:3001/patientRegistration",{
         method: "POST",
         headers:{
          'Accept' : 'application/json',
          'Content-Type' : 'application/json'
      },
      body: JSON.stringify(patient)
    }).then((result)=>{
      console.warn("result", result);
    })
    alert("data submited");
    window.location.reload();

};

const loadPatient = async()=>{
const result = await axios.get(`http://localhost:3001/patientRegistration/${patient_id}`);
setPatient(result.data);
}

  // function refreshPage() {
	// 	window.location.reload(false);
	// }

  return (
    <Container style={{marginTop:150, marginLeft:-60}}>
      <Header  />
      <div className="VCLabel"  >
        <img src={logo} alt=" " style={{height:100,width:100, marginLeft:130}} />
 <Typography style={{fontFamily:"Carter One",color:"#fff",fontSize:50,marginLeft:70}} >
        Patient
      </Typography>
      
      </div>
    <Card className="registration">
    <CardMedia
        className={classes.cover}
        // image="https://images.pexels.com/photos/5412/water-blue-ocean.jpg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
        // title="Live from space album cover"
        style={{backgroundColor:"#41649c"}}
      />
      
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography style={{fontSize:30, marginBottom:20,fontFamily: "Carter One", color: "#41649c"}}>
            Update Patient Form
          </Typography>
        <Form onSubmit={e => onSubmit(e)}>
  <Form.Row>
  <Form.Group as={Col} controlId="patient_id">
      <Form.Label className="patient_id">Patient-id</Form.Label>
      <Form.Control type="text" name="patient_id" value={patient_id} onChange={e => onInputChange(e)} placeholder="Enter ID" />
    </Form.Group>
    <Form.Group as={Col} controlId="VC_id">
      <Form.Label className="VC_id">Vaccination Center ID</Form.Label>
      <Form.Control type="text" name="vc_id" defaultValue={vc_id} onChange={(e) => setVC_id(e.target.defaultValue)} placeholder="Enter ID" />
    </Form.Group>
  </Form.Row>

  <Form.Row>
  <Form.Group as={Col} controlId="f_name">
      <Form.Label className="f_name">FirstName</Form.Label>
      <Form.Control type="text" name="f_name" defaultValue={f_name} onChange={(e) => setFname(e.target.defaultValue)}  placeholder="Enter FirstName" />
    </Form.Group>

    <Form.Group as={Col} controlId="l_name">
      <Form.Label className="l_name">LastName</Form.Label>
      <Form.Control type="text" name="l_name" defaultValue={l_name}  onChange={(e) => setLname(e.target.defaultValue)}  placeholder="Enter LastName" />
    </Form.Group>
  </Form.Row>
  <Form.Row>
  <Form.Group as={Col} controlId="father_name">
      <Form.Label className="father_name">Father's Name</Form.Label>
      <Form.Control type="text" name="father_name" defaultValue={father_name} onChange={(e) => setFather_name(e.target.defaultValue)}  placeholder="Enter FatherName" />
    </Form.Group>
  </Form.Row>
  
  <Form.Group controlId="email">
    <Form.Label className="email">Email</Form.Label>
    <Form.Control type="Email" name="email" defaultValue={email} onChange={(e) => setEmail(e.target.defaultValue)}  placeholder=" Enter Email" />
    </Form.Group>
  

 <Form.Row>
    <Form.Group as={Col} controlId="password">
      <Form.Label className="password">Password</Form.Label>
      <Form.Control type="Password" name="password" defaultValue={password} onChange={(e) => setPassword(e.target.defaultValue)}  placeholder="Enter Password" />
    </Form.Group>
    </Form.Row>
  <Form.Row>
    <Form.Group as={Col} controlId="phone">
      <Form.Label className="phone">Phone#</Form.Label>
      <Form.Control type="text" name="phone" defaultValue={phone} onChange={(e) => setPhone(e.target.defaultValue)}  placeholder="Enter Phone Number" />
    </Form.Group>

    <Form.Group as={Col} controlId="qualification">
    <Form.Label className="qualification">Qualification</Form.Label>
      <Form.Control type="text" name="qualification" defaultValue={qualification} onChange={(e) => setQualification(e.target.defaultValue)}  placeholder="Enter Qualification" />
    </Form.Group>
  </Form.Row>
  <Form.Row>
  <Form.Group as={Col} controlId="date">
    <Form.Label className="date">Date Of Registration</Form.Label>
      <Form.Control type="text" name="date" defaultValue={date} onChange={(e) => setDate(e.target.defaultValue)}  placeholder="i.e: DD-MM-YYYY" />
    </Form.Group>
    </Form.Row>
  <Form.Row>
    <Form.Group as={Col} controlId="gender">
      <Form.Label className="gender">Gender</Form.Label>
      <Form.Control type="text" name="gender" defaultValue={gender} onChange={(e) => setGender(e.target.defaultValue)}  placeholder="Enter Gender" />
    </Form.Group>
    <Form.Group as={Col} controlId="dob">
      <Form.Label className="dob">DOB</Form.Label>
      <Form.Control type="text" name="dob" defaultValue={dob}  onChange={(e) => setDOB(e.target.defaultValue)}  placeholder="Enter DOB" />
    </Form.Group>
    <Form.Group as={Col} controlId="cnic">
      <Form.Label className="cnic">CNIC</Form.Label>
      <Form.Control type="text" name="cnic" defaultValue={cnic} onChange={(e) => setCNIC(e.target.defaultValue)}  placeholder="Enter CNIC" />
    </Form.Group>
  </Form.Row>
<Form.Row>
<Form.Group as={Col} controlId="martial_status">
      <Form.Label className="ms">Martial Status</Form.Label>
      <Form.Control type="text" name="martial_status" defaultValue={martial_status} onChange={(e) => setMartial_Status(e.target.defaultValue)}  placeholder="Enter Martial Status" />
    </Form.Group>
<Form.Group as={Col} controlId="religion">
      <Form.Label className="religion">Religion</Form.Label>
      <Form.Control type="text" name="religion" defaultValue={religion} onChange={(e) => setReligion(e.target.defaultValue)}  placeholder="Enter Religion" />
    </Form.Group>
</Form.Row>
<Form.Group controlId="address">
    <Form.Label className="address">Address</Form.Label>
    <Form.Control type="text" name="address" defaultValue={address} onChange={(e) => setAddress(e.target.defaultValue)}  placeholder=" 1234 Main St Apartment, studio, or floor" />
  </Form.Group>
  <Form.Row>
    <Form.Group as={Col} controlId="city">
      <Form.Label className="city">City</Form.Label>
      <Form.Control type="text" name="city" defaultValue={city} onChange={(e) => setCity(e.target.defaultValue)}  placeholder="Enter City" />
    </Form.Group>

    <Form.Group as={Col} controlId="state">
      <Form.Label className="state">State</Form.Label>
      <Form.Control type="text" name="state" defaultValue={state} onChange={(e) => setState(e.target.defaultValue)}  placeholder="Enter state" />
    </Form.Group>

    <Form.Group as={Col} controlId="zip">
      <Form.Label className="zip">Zip</Form.Label>
      <Form.Control type="text" name="zip" defaultValue={zip} onChange={(e) => setZip(e.target.defaultValue)}  placeholder="Enter Zip/Postal Code" />
    </Form.Group>
  </Form.Row>

  <Button variant="primary" type="submit" >
  Update Record
  </Button>
</Form>
         
        </CardContent>
        
      </div>
    
    </Card>
    </Container>
  );
}
